import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import detectEthereumProvider from "@metamask/detect-provider";
import {Contract, ethers} from "ethers";
import { useState, useEffect, useRef } from 'react';
import realStateContractManifest from "./contracts/RealStateContract.json";
function App(){
  const realState = useRef(null);
    useEffect( () => {
        initContracts();
    }, [])

    let initContracts = async () => {
        await getBlockchain();
    }

    let getBlockchain = async () => {
        let provider = await detectEthereumProvider();
        if(provider) {
            await provider.request({ method: 'eth_requestAccounts' });
            const networkId = await provider.request({ method: 'net_version' })

            provider = new ethers.providers.Web3Provider(provider);
            const signer = provider.getSigner();
            realState.current = new Contract(
              realStateContractManifest.networks[networkId].address,
              realStateContractManifest.abi,
              signer
          );

        }
        return null;
    }
    let onSubmitAddRealState = async (e) => {
      e.preventDefault();

      let city = e.target.elements[0].value;
      let street = e.target.elements[1].value;
      let number = parseInt(e.target.elements[2].value)
      let meters = parseInt(e.target.elements[3].value)
      let registration = parseInt(e.target.elements[4].value)
      let owner = e.target.elements[5].value;

      // this function use gas
      const tx = await realState.current.addRealState(city,street,number,meters,registration,owner);

      await tx.wait();
  }


    return (
        <div>
            <h1>RealState</h1>
            <h2>Add RealState</h2>
        <form onSubmit= { (e) => onSubmitAddRealState(e) } >
            <input type="text" placeholder="city"/>
            <input type="text" placeholder="street"/>
            <input type="number" placeholder="number"/>
            <input type="number" placeholder="meters"/>
            <input type="number" placeholder="registration"/>
            <input type="text" placeholder="owner name"/>
            <button type="submit">Add</button>
        </form>

        </div>
    )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <App />
);
